import pandas as pd
from pgmpy.estimators import MaximumLikelihoodEstimator
from pgmpy.inference import VariableElimination
from pgmpy.models import BayesianModel

data=pd.read_csv("heartdisease.csv")
heart_disease=pd.DataFrame(data)
print(heart_disease)

model=BayesianModel([('age', 'Lifestyle'),
('Gender', 'Lifestyle'),
('Family', 'heartdisease'),
('diet', 'cholestrol'),
('Lifestyle', 'diet'),
('cholestrol', 'heartdisease'),
('diet', 'cholestrol')
])

model.fit(heart_disease, estimator=MaximumLikelihoodEstimator)
HeartDisease_infer = VariableElimination(model)

print('For age Enter { SuperSeniorCitizen:0, SeniorCitizen:1, MiddleAged:2, Youth:3, Teen:4')
print('For Gender Enter { Male:0, Female:1 }')
print('For Family History Enter { yes:1, No:0 }')
print('For diet Enter { High:0, Medium:1 }')
print('For lifestyle Enter { Athlete:0, Active:1, Moderate:2, Sedentary:3 }')
print('For cholesterol Enter { High:0, BorderLine:1, Normal:2 }')

q = HeartDisease_infer.query(variables=['heartdisease'], evidence={
    'age': int(input('Enter age (0-4): ')), # Assuming age is encoded as 0-4
    'Gender': int(input('Enter Gender (0 or 1): ')), # Gender as e or 1
    'Family': int(input('Enter Family History (0 or 1): ')), # Family history as e or 'diet': int(input('Enter Diet (0-3): ')),
    'diet': int(input('Enter Diet (0-3): ')),
    'Lifestyle': int(input('Enter Lifestyle (0-3): ')),
    'cholestrol': int(input('Enter cholestrol (0-2): '))
})

print("+----------------+---------------------+")
print("I heartdisease I phi(heartdisease) I")
print("+----------------+---------------------+")
for i, prob in enumerate(q.values):
    # Constructing state names Like 'heartdisease_e', 'heartdisease_l', etc.
    state_name = f"heartdisease_{i}"
    print(f"I {state_name:15} I {prob:17.4f} I")
print("+----------------+---------------------+")


